import chalk from "chalk";
if(process.argv.length<3){
    console.error(chalk.red('ERROR: вы не ввели длину в дюймах'))
    process.exit(1)
}
var d=process.argv[2]
d=parseFloat(d)
if(!d){
    console.error(chalk.red('ERROR: вы ввели длину не в том формате'))
    process.exit(1)
}
const cm=d*2.54
console.log(chalk.blue('d = '+d)+' >>> '+chalk.green(cm+' см.'))